public class JavaOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 5;
	    System.out.println(x > 3 && x < 10);
	    
	    
	    int y = 5;
	    System.out.println(y > 3 || y< 4);
	    
	    
	    int Z = 5;
	    System.out.println(!(Z > 3 && Z < 10));
	    
	    int a = 5;
	    int b = 3;
	    System.out.println(a > b); // returns true because 5 is greater than 3
	    
	    
	    int a1 = 5;
	    int b1 = 3;
	    System.out.println(a1 < b1); 
	    
	    
	    int a2 = 5;
	    int b2 = 3;
	    System.out.println(a2 >= b2); // returns true because 5 is greater, or equal, to 3
	  
	    int a3 = 5;
	    int b3 = 3;
	    System.out.println(a3 <= b3); // returns false because 5 is neither less than or equal to 3
	    
	    

	}
}

